import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import ResponseDisplay from './components/ResponseDisplay';
import { 
  Container, TextField, Button, Typography, CircularProgress, Paper,
  Box, IconButton, List, ListItem, ListItemText, Divider
} from '@mui/material';
import PhotoCamera from '@mui/icons-material/PhotoCamera';
import DeleteIcon from '@mui/icons-material/Delete';

const App = () => {
  const [mode, setMode] = useState('restaurant');
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [response, setResponse] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);
  const [chatHistory, setChatHistory] = useState([]);
  const sessionId = 'user123'; // In a real app, this would be dynamic

  // Fetch chat history on component mount
  useEffect(() => {
    fetchChatHistory();
  }, []);

  const fetchChatHistory = async () => {
    try {
      const res = await fetch(`http://localhost:8000/chat_history/${sessionId}`);
      const data = await res.json();
      setChatHistory(data.reverse()); // Show newest messages first
    } catch (error) {
      console.error('Error fetching chat history:', error);
    }
  };

  const clearChatHistory = async () => {
    try {
      await fetch(`http://localhost:8000/chat_history/${sessionId}`, {
        method: 'DELETE'
      });
      setChatHistory([]);
      setResponse('');
    } catch (error) {
      console.error('Error clearing chat history:', error);
    }
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.size > 5 * 1024 * 1024) { // 5MB limit
      alert('Image size should be less than 5MB');
      return;
    }
    setSelectedImage(file);
  };

  const handleSubmit = async () => {
    setLoading(true);
    try {
      let endpoint = mode === 'restaurant' ? '/restaurant/query' : '/clinic/query';
      let body;

      if (selectedImage && mode === 'restaurant') {
        const formData = new FormData();
        formData.append('image', selectedImage);
        formData.append('request', JSON.stringify({
          query,
          session_id: sessionId
        }));

        endpoint = '/analyze_menu_image';
        const res = await fetch(`http://localhost:8000${endpoint}`, {
          method: 'POST',
          body: formData
        });
        const data = await res.json();
        setResponse(data.response);
      } else {
        const res = await fetch(`http://localhost:8000${endpoint}`, {
          method: 'POST',
          headers: {'Content-Type': 'application/json'},
          body: JSON.stringify({ 
            query, 
            session_id: sessionId,
            preferences: {} // Can be extended for user preferences
          }),
        });
        const data = await res.json();
        setResponse(data.response);
      }

      // Refresh chat history after new response
      fetchChatHistory();
    } catch (error) {
      console.error('Error:', error);
      setResponse('Error processing your request.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md" sx={{ height: '100vh', py: 2 }}>
      <Header mode={mode} setMode={setMode} />
      <Box sx={{ display: 'flex', gap: 2, mt: 2, height: 'calc(100vh - 100px)' }}>
        {/* Chat History Panel */}
        <Paper 
          elevation={3} 
          sx={{ 
            width: '30%', 
            p: 2, 
            height: '100%',
            display: 'flex',
            flexDirection: 'column'
          }}
        >
          <Box sx={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center', 
            mb: 2,
            flexShrink: 0
          }}>
            <Typography variant="h6">Chat History</Typography>
            {chatHistory.length > 0 && (
              <Button 
                size="small" 
                onClick={clearChatHistory}
                color="error"
                variant="outlined"
              >
                Clear History
              </Button>
            )}
          </Box>
          <List sx={{ 
            overflowY: 'auto', 
            flexGrow: 1,
            '&::-webkit-scrollbar': {
              width: '8px',
            },
            '&::-webkit-scrollbar-track': {
              background: '#f1f1f1',
            },
            '&::-webkit-scrollbar-thumb': {
              background: '#888',
              borderRadius: '4px',
            },
            '&::-webkit-scrollbar-thumb:hover': {
              background: '#555',
            },
          }}>
            {chatHistory.map((chat, index) => (
              <React.Fragment key={index}>
                <ListItem>
                  <ListItemText
                    primary={<Typography variant="subtitle2">You: {chat.user}</Typography>}
                    secondary={
                      <Typography variant="body2" color="text.secondary">
                        Assistant: {chat.assistant}
                      </Typography>
                    }
                  />
                </ListItem>
                <Divider />
              </React.Fragment>
            ))}
          </List>
        </Paper>

        {/* Main Interaction Panel */}
        <Paper 
          elevation={3} 
          sx={{ 
            width: '70%', 
            p: 3,
            height: '100%',
            display: 'flex',
            flexDirection: 'column'
          }}
        >
          <Box sx={{ flexShrink: 0 }}>
            <Typography variant="h6" gutterBottom>Ask your question:</Typography>
            <TextField
              fullWidth
              multiline
              rows={4}
              variant="outlined"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              sx={{ mb: 2 }}
            />

            {/* Image Upload Section */}
            {mode === 'restaurant' && (
              <Box sx={{ mb: 2, display: 'flex', alignItems: 'center', gap: 1 }}>
                <input
                  accept="image/*"
                  style={{ display: 'none' }}
                  id="image-upload"
                  type="file"
                  onChange={handleImageUpload}
                />
                <label htmlFor="image-upload">
                  <IconButton color="primary" component="span">
                    <PhotoCamera />
                  </IconButton>
                </label>
                {selectedImage && (
                  <>
                    <Typography variant="body2">
                      {selectedImage.name}
                    </Typography>
                    <IconButton 
                      size="small" 
                      onClick={() => setSelectedImage(null)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </>
                )}
              </Box>
            )}

            <Button
              variant="contained"
              onClick={handleSubmit}
              disabled={loading || (!query && !selectedImage)}
              fullWidth
              sx={{ mb: 2 }}
            >
              {loading ? <CircularProgress size={24} /> : 'Submit'}
            </Button>
          </Box>

          <Box sx={{ 
            flexGrow: 1, 
            overflowY: 'auto',
            '&::-webkit-scrollbar': {
              width: '8px',
            },
            '&::-webkit-scrollbar-track': {
              background: '#f1f1f1',
            },
            '&::-webkit-scrollbar-thumb': {
              background: '#888',
              borderRadius: '4px',
            },
            '&::-webkit-scrollbar-thumb:hover': {
              background: '#555',
            },
          }}>
            {response && <ResponseDisplay response={response} />}
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default App;
