import React from 'react';
import { Paper, Typography, List, ListItem, ListItemText, Box } from '@mui/material';

const ResponseDisplay = ({ response }) => {
  // Format the response text by splitting into lines and processing each line
  const formatResponse = (text) => {
    const lines = text.split('\n');
    return lines.map((line, index) => {
      // Check if line contains price
      if (line.includes('$')) {
        const [itemName, ...rest] = line.split(' - ');
        const description = rest.join(' - ');
        return (
          <ListItem key={index} sx={{ flexDirection: 'column', alignItems: 'flex-start' }}>
            <Typography variant="subtitle1" color="primary" sx={{ fontWeight: 'bold' }}>
              {itemName}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {description}
            </Typography>
          </ListItem>
        );
      }
      // For headers or other text
      return (
        <Typography 
          key={index} 
          variant={line.includes(':') ? 'h6' : 'body1'}
          sx={{ mb: 1, fontWeight: line.includes(':') ? 'bold' : 'normal' }}
        >
          {line}
        </Typography>
      );
    });
  };

  return (
    <Paper elevation={1} sx={{ p: 2, mt: 2, bgcolor: '#f5f5f5' }}>
      <Box sx={{ maxWidth: '100%' }}>
        {formatResponse(response)}
      </Box>
    </Paper>
  );
};

export default ResponseDisplay;
