import React from 'react';
import { Paper, Typography, List, ListItem, Box } from '@mui/material';
import ReactMarkdown from 'react-markdown';

const ResponseDisplay = ({ response }) => {
  return (
    <Paper elevation={1} sx={{ p: 2, mt: 2, bgcolor: '#f5f5f5' }}>
      <Box sx={{ maxWidth: '100%' }}>
        <ReactMarkdown
          components={{
            h1: ({ children }) => <Typography variant="h4" gutterBottom>{children}</Typography>,
            h2: ({ children }) => <Typography variant="h5" gutterBottom>{children}</Typography>,
            h3: ({ children }) => <Typography variant="h6" gutterBottom>{children}</Typography>,
            p: ({ children }) => <Typography variant="body1" paragraph>{children}</Typography>,
            strong: ({ children }) => <Typography component="span" sx={{ fontWeight: 'bold' }}>{children}</Typography>,
            em: ({ children }) => <Typography component="span" sx={{ fontStyle: 'italic' }}>{children}</Typography>,
            ul: ({ children }) => <List>{children}</List>,
            li: ({ children }) => (
              <ListItem sx={{ display: 'list-item', pl: 2 }}>
                <Typography variant="body1">{children}</Typography>
              </ListItem>
            ),
          }}
        >
          {response}
        </ReactMarkdown>
      </Box>
    </Paper>
  );
};

export default ResponseDisplay;
