import React, { useState } from 'react';
import Header from './components/Header';
import ResponseDisplay from './components/ResponseDisplay';
import { Container, TextField, Button, Typography, CircularProgress, Paper } from '@mui/material';

const App = () => {
  const [mode, setMode] = useState('restaurant');
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [response, setResponse] = useState('');

  const handleSubmit = async () => {
    setLoading(true);
    try {
      const endpoint = mode === 'restaurant' ? '/restaurant/query' : '/clinic/query';
      const res = await fetch(endpoint, {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({ query, session_id: 'user123' }),
      });
      const data = await res.json();
      setResponse(data.response);
    } catch (error) {
      setResponse('Error processing your request.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md">
      <Header mode={mode} setMode={setMode} />
      <Paper elevation={3} sx={{ padding: 3, marginTop: 2 }}>
        <Typography variant="h6">Ask your question:</Typography>
        <TextField
          fullWidth
          multiline
          rows={4}
          variant="outlined"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder={`Ask about ${mode === 'restaurant' ? 'menu items' : 'doctor availability'}...`}
        />
        <Button
          variant="contained"
          onClick={handleSubmit}
          disabled={!query || loading}
          sx={{ marginTop: 2 }}
        >
          {loading ? <CircularProgress size={24} /> : 'Submit'}
        </Button>
        <ResponseDisplay response={response} />
      </Paper>
    </Container>
  );
};

export default App;
